targets.expr:
topk(${top}, 
    sum by (host_name,method,uri,status) (rate(http_server_requests_milliseconds_count{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", uri=~"${uri}", status=~"${status}"}[$__rate_interval]))
)

topk(${top}, 
    sum by(host_name, method, uri, status) (rate(http_server_requests_milliseconds_count{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", uri=~"${uri}", status!~"[1-3]..", status=~"${status}"}[$__rate_interval]))
)

topk(${top}, 
    histogram_quantile(${quantile:value}, sum by (le,host_name,status,uri,method) (rate(http_server_requests_milliseconds_bucket{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", uri=~"${uri}", status=~"${status}"}[$__rate_interval])))
)

topk(${top}, 
    sum by (host_name) (jvm_memory_used_bytes{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="heap"})
    * 100 /
    sum by (host_name) (jvm_memory_max_bytes{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="heap"}) 
    > ${saturation:value}
)

topk(${top}, 
    sum by (host_name) (jvm_memory_used_bytes{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="nonheap"})
    * 100 / 
    sum by (host_name) (jvm_memory_max_bytes{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="nonheap"}) 
    > ${saturation:value}
)
