targets.expr:
topk(${top}, 
    sum by(service_instance_id, method, uri, status) (rate(http_server_requests_count{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", status!~"[1-3].."}[1m]))
)

topk(${top}, 
    sum by (service_instance_id) (jvm_memory_used{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", area="heap"})
    * 100 /
    sum by (service_instance_id) (jvm_memory_max{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", area="heap"}) 
    > ${saturation:value}
)

topk(${top}, 
    sum by (service_instance_id) (jvm_memory_used{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", area="nonheap"})
    * 100 / 
    sum by (service_instance_id) (jvm_memory_max{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", area="nonheap"}) 
    > ${saturation:value}
)
